from flask import Flask, render_template, session, request, redirect
app = Flask(__name__)
app.secret_key = "countersession"
app.count = 0

@app.route("/")
def index():
    if "count" in session.keys():
        session["count"] += 1
    else:
        session["count"] = 0
    print(session["count"])
    return render_template("counterindex.html", count = session["count"])

@app.route("/increment", methods=["POST"])
def increment_by_two():
    session["count"] += 1
    return redirect("/")

